const KITCHEN_CONFIG = {
    BRIGHTNESS: {
        DAY: 220,
        NIGHT: 80,
        INCREMENT_PERCENT: 12,
        MIN: 10,
        MAX: 255,
        DEFAULT: 220
    },
    TIMERS: {
        AUTO_OFF_MINUTES: 15,
        MANUAL_OVERRIDE_MINUTES: 45,
        HEALTH_CHECK_MINUTES: 5
    },
    THRESHOLDS: {
        LIGHT_SENSOR_LUX: 25
    },
    ENTITIES: {
        LIGHT: 'light.kitchen',
        MOTION_SENSOR: 'binary_sensor.sensor_kitchen_motion',
        ILLUMINANCE_SENSOR: 'sensor.sensor_kitchen_illuminance',
        SCENE_ENERGIZE: 'scene.kitchen_energize',
        SCENE_DIMMED: 'scene.kitchen_dimmed'
    },
    EVENTS: {
        ON_SHORT: 1002,
        DIM_UP_SHORT: 2002,
        DIM_DOWN_SHORT: 3002,
        OFF_SHORT: 4002,
        ON_HOLD: 1001,
        OFF_HOLD: 4001
    }
};

const validateBrightness = (brightness) => {
    if (typeof brightness !== 'number' || isNaN(brightness) || brightness < KITCHEN_CONFIG.BRIGHTNESS.MIN || brightness > KITCHEN_CONFIG.BRIGHTNESS.MAX) {
        return KITCHEN_CONFIG.BRIGHTNESS.DEFAULT;
    }
    return Math.round(brightness);
};

const calculateBrightnessChange = (current, isIncrease) => {
    const change = Math.round(current * (KITCHEN_CONFIG.BRIGHTNESS.INCREMENT_PERCENT / 100));
    const minChange = 15;
    const actualChange = Math.max(change, minChange);

    if (isIncrease) {
        return Math.min(current + actualChange, KITCHEN_CONFIG.BRIGHTNESS.MAX);
    } else {
        return Math.max(current - actualChange, KITCHEN_CONFIG.BRIGHTNESS.MIN);
    }
};

const logEvent = (message, level = 'info') => {
    const timestamp = new Date().toISOString();
    const logMessage = `[${timestamp}] Kitchen: ${message}`;

    switch(level) {
        case 'error': node.error(logMessage); break;
        case 'warn': node.warn(logMessage); break;
        default: node.log(logMessage);
    }
};

const validateKitchenState = () => {
    try {
        const brightness = flow.get('kitchenCurrentBrightness');
        const validBrightness = validateBrightness(brightness);

        if (brightness !== validBrightness) {
            flow.set('kitchenCurrentBrightness', validBrightness);
            logEvent(`Brightness corrected from ${brightness} to ${validBrightness}`, 'warn');
        }

        if (flow.get('kitchenAutomationEnabled') === undefined) {
            flow.set('kitchenAutomationEnabled', true);
        }
        if (flow.get('kitchenManualOverride') === undefined) {
            flow.set('kitchenManualOverride', false);
        }

        return true;
    } catch (error) {
        logEvent(`State validation failed: ${error.message}`, 'error');
        return false;
    }
};

const validateEntity = async (entityId, entityType = 'unknown') => {
    try {
        const state = await global.get('homeassistant').homeAssistant.states.get(entityId);
        if (!state) {
            logEvent(`Entity validation failed: ${entityId} (${entityType}) not found`, 'error');
            return false;
        }
        logEvent(`Entity validated: ${entityId} (${entityType}) - state: ${state.state}`);
        return true;
    } catch (error) {
        logEvent(`Entity validation error for ${entityId}: ${error.message}`, 'error');
        return false;
    }
};

global.set('KITCHEN_CONFIG', KITCHEN_CONFIG);
global.set('kitchenUtils', {
    validateBrightness,
    calculateBrightnessChange,
    logEvent,
    validateKitchenState,
    validateEntity
});

validateKitchenState();
logEvent('Kitchen automation initialized with comprehensive error fixes');

return null;

try {
    const CONFIG = global.get('KITCHEN_CONFIG');
    const utils = global.get('kitchenUtils');

    if (!CONFIG || !utils) {
        node.error('Configuration not loaded');
        return null;
    }

    if (!msg.payload || !msg.payload.event) {
        utils.logEvent('Invalid dimmer event payload', 'error');
        return null;
    }

    const event = msg.payload.event;
    const currentBrightness = utils.validateBrightness(flow.get('kitchenCurrentBrightness') || CONFIG.BRIGHTNESS.DEFAULT);

    utils.logEvent(`Processing dimmer event: ${event}`);

    let serviceCall = null;

    switch(event) {
        case CONFIG.EVENTS.ON_SHORT:
            flow.set('kitchenManualOverride', true);
            serviceCall = {
                domain: 'light',
                service: 'toggle',
                target: { entity_id: CONFIG.ENTITIES.LIGHT }
            };
            utils.logEvent('Toggle light - manual override activated');
            break;

        case CONFIG.EVENTS.DIM_UP_SHORT:
            flow.set('kitchenManualOverride', true);
            const newBrightUp = utils.calculateBrightnessChange(currentBrightness, true);
            flow.set('kitchenCurrentBrightness', newBrightUp);
            serviceCall = {
                domain: 'light',
                service: 'turn_on',
                target: { entity_id: CONFIG.ENTITIES.LIGHT },
                service_data: { brightness: newBrightUp, transition: 1 }
            };
            utils.logEvent(`Brightness increased from ${currentBrightness} to ${newBrightUp}`);
            break;

        case CONFIG.EVENTS.DIM_DOWN_SHORT:
            flow.set('kitchenManualOverride', true);
            const newBrightDown = utils.calculateBrightnessChange(currentBrightness, false);
            flow.set('kitchenCurrentBrightness', newBrightDown);
            serviceCall = {
                domain: 'light',
                service: 'turn_on',
                target: { entity_id: CONFIG.ENTITIES.LIGHT },
                service_data: { brightness: newBrightDown, transition: 1 }
            };
            utils.logEvent(`Brightness decreased from ${currentBrightness} to ${newBrightDown}`);
            break;

        case CONFIG.EVENTS.OFF_SHORT:
            flow.set('kitchenManualOverride', true);
            serviceCall = {
                domain: 'light',
                service: 'turn_off',
                target: { entity_id: CONFIG.ENTITIES.LIGHT },
                service_data: { transition: 1 }
            };
            utils.logEvent('Light turned off - manual override activated');
            break;

        case CONFIG.EVENTS.ON_HOLD:
            flow.set('kitchenAutomationEnabled', false);
            flow.set('kitchenManualOverride', true);
            serviceCall = {
                domain: 'light',
                service: 'turn_on',
                target: { entity_id: CONFIG.ENTITIES.LIGHT },
                service_data: { brightness: CONFIG.BRIGHTNESS.MAX, transition: 1 }
            };
            utils.logEvent('Automation disabled via hold gesture', 'warn');
            break;

        case CONFIG.EVENTS.OFF_HOLD:
            flow.set('kitchenAutomationEnabled', true);
            flow.set('kitchenManualOverride', false);
            serviceCall = {
                domain: 'light',
                service: 'turn_off',
                target: { entity_id: CONFIG.ENTITIES.LIGHT },
                service_data: { transition: 1 }
            };
            utils.logEvent('Automation enabled via hold gesture');
            break;

        default:
            utils.logEvent(`Unknown dimmer event: ${event}`, 'warn');
            return null;
    }

    if (serviceCall) {
        msg.payload = serviceCall;
        msg.timestamp = Date.now();
        return msg;
    }

    return null;

} catch (error) {
    const utils = global.get('kitchenUtils');
    if (utils) {
        utils.logEvent(`Error processing dimmer event: ${error.message}`, 'error');
    } else {
        node.error(`Critical error processing dimmer event: ${error.message}`);
    }
    return null;
}

try {
    const utils = global.get('kitchenUtils');
    const automationEnabled = flow.get('kitchenAutomationEnabled') !== false;

    if (automationEnabled) {
        flow.set('kitchenManualOverride', false);
        if (utils) {
            utils.logEvent('Manual override cleared after timeout');
        }
    } else {
        if (utils) {
            utils.logEvent('Manual override timeout ignored - automation disabled');
        }
    }

    return null;
} catch (error) {
    const utils = global.get('kitchenUtils');
    if (utils) {
        utils.logEvent(`Error clearing override: ${error.message}`, 'error');
    } else {
        node.error(`Critical error clearing override: ${error.message}`);
    }
    return null;
}

try {
    const utils = global.get('kitchenUtils');

    if (!utils) {
        node.error('Kitchen utilities not available');
        return [null, msg];
    }

    utils.validateKitchenState();

    const automationEnabled = flow.get('kitchenAutomationEnabled') !== false;
    const manualOverride = flow.get('kitchenManualOverride') === true;

    utils.logEvent(`Motion detected - Automation: ${automationEnabled}, Override: ${manualOverride}`);

    if (automationEnabled && !manualOverride) {
        utils.logEvent('Motion automation proceeding');
        return [msg, null];
    } else {
        const reason = !automationEnabled ? 'automation disabled' : 'manual override active';
        utils.logEvent(`Motion automation blocked: ${reason}`, 'warn');
        return [null, msg];
    }

} catch (error) {
    const utils = global.get('kitchenUtils');
    if (utils) {
        utils.logEvent(`Error checking automation state: ${error.message}`, 'error');
    } else {
        node.error(`Critical error checking automation state: ${error.message}`);
    }
    return [null, msg];
}

try {
    const CONFIG = global.get('KITCHEN_CONFIG');
    const utils = global.get('kitchenUtils');

    if (!CONFIG || !utils) {
        node.error('Configuration not available');
        return [null, null];
    }

    // Get current state of illuminance sensor
    const illuminanceEntity = global.get('homeassistant').homeAssistant.states.get(CONFIG.ENTITIES.ILLUMINANCE_SENSOR);

    if (!illuminanceEntity) {
        utils.logEvent(`Illuminance sensor ${CONFIG.ENTITIES.ILLUMINANCE_SENSOR} not found - proceeding with daytime brightness`, 'warn');
        msg.current_lux = 'unavailable';
        msg.threshold = CONFIG.THRESHOLDS.LIGHT_SENSOR_LUX;
        return [null, msg]; // Proceed to turn on lights if sensor unavailable
    }

    const currentLux = parseFloat(illuminanceEntity.state);

    if (isNaN(currentLux)) {
        utils.logEvent(`Invalid illuminance reading: ${illuminanceEntity.state} - proceeding with daytime brightness`, 'warn');
        msg.current_lux = 'invalid';
        msg.threshold = CONFIG.THRESHOLDS.LIGHT_SENSOR_LUX;
        return [null, msg]; // Proceed to turn on lights if reading invalid
    }

    msg.current_lux = currentLux;
    msg.threshold = CONFIG.THRESHOLDS.LIGHT_SENSOR_LUX;

    if (currentLux >= CONFIG.THRESHOLDS.LIGHT_SENSOR_LUX) {
        utils.logEvent(`Motion blocked - too bright for daytime automation (${currentLux} lux â‰¥ ${CONFIG.THRESHOLDS.LIGHT_SENSOR_LUX} lux threshold)`, 'info');
        return [msg, null]; // Block - too bright
    } else {
        utils.logEvent(`Light level check passed - proceeding with daytime automation (${currentLux} lux < ${CONFIG.THRESHOLDS.LIGHT_SENSOR_LUX} lux threshold)`);
        return [null, msg]; // Proceed - dark enough
    }

} catch (error) {
    const utils = global.get('kitchenUtils');
    if (utils) {
        utils.logEvent(`Error in light level check: ${error.message}`, 'error');
    } else {
        node.error(`Critical error in light level check: ${error.message}`);
    }
    // On error, proceed with caution - turn on lights
    msg.current_lux = 'error';
    msg.threshold = 25;
    return [null, msg];
}

try {
    const CONFIG = global.get('KITCHEN_CONFIG');
    const utils = global.get('kitchenUtils');

    if (!CONFIG || !utils) {
        node.error('Configuration not available');
        return null;
    }

    const brightness = CONFIG.BRIGHTNESS.DAY;
    const lux = msg.current_lux || 'unknown';
    utils.logEvent(`Setting daytime brightness: ${brightness} (current light: ${lux} lux < ${CONFIG.THRESHOLDS.LIGHT_SENSOR_LUX} lux threshold)`);

    // Store brightness in flow for tracking
    flow.set('kitchenCurrentBrightness', brightness);

    msg.payload = {
        domain: 'light',
        service: 'turn_on',
        target: { entity_id: CONFIG.ENTITIES.LIGHT },
        service_data: { brightness: brightness, transition: 2 }
    };

    msg.brightness_set = brightness;
    msg.mode = 'daytime';

    return msg;

} catch (error) {
    const utils = global.get('kitchenUtils');
    if (utils) {
        utils.logEvent(`Error setting daytime brightness: ${error.message}`, 'error');
    } else {
        node.error(`Critical error setting daytime brightness: ${error.message}`);
    }
    return null;
}

try {
    const CONFIG = global.get('KITCHEN_CONFIG');
    const utils = global.get('kitchenUtils');

    if (!CONFIG || !utils) {
        node.error('Configuration not available');
        return null;
    }

    const brightness = CONFIG.BRIGHTNESS.NIGHT;
    utils.logEvent(`Setting nighttime brightness: ${brightness}`);

    // Store brightness in flow for tracking
    flow.set('kitchenCurrentBrightness', brightness);

    msg.payload = {
        domain: 'light',
        service: 'turn_on',
        target: { entity_id: CONFIG.ENTITIES.LIGHT },
        service_data: { brightness: brightness, transition: 2 }
    };

    msg.brightness_set = brightness;
    msg.mode = 'nighttime';

    return msg;

} catch (error) {
    const utils = global.get('kitchenUtils');
    if (utils) {
        utils.logEvent(`Error setting nighttime brightness: ${error.message}`, 'error');
    } else {
        node.error(`Critical error setting nighttime brightness: ${error.message}`);
    }
    return null;
}

try {
    const utils = global.get('kitchenUtils');

    if (!utils) {
        node.error('Kitchen utilities not available');
        return [null, msg];
    }

    utils.validateKitchenState();

    const manualOverride = flow.get('kitchenManualOverride') === true;
    const automationEnabled = flow.get('kitchenAutomationEnabled') !== false;

    utils.logEvent(`Auto-off timer triggered - Automation: ${automationEnabled}, Override: ${manualOverride}`);

    if (automationEnabled && !manualOverride) {
        utils.logEvent('Auto-off proceeding');
        return [msg, null];
    } else {
        const reason = !automationEnabled ? 'automation disabled' : 'manual override active';
        utils.logEvent(`Auto-off cancelled: ${reason}`, 'warn');
        return [null, msg];
    }

} catch (error) {
    const utils = global.get('kitchenUtils');
    if (utils) {
        utils.logEvent(`Error in auto-off check: ${error.message}`, 'error');
    } else {
        node.error(`Critical error in auto-off check: ${error.message}`);
    }
    return [null, msg];
}

try {
    const utils = global.get('kitchenUtils');
    if (utils) {
        utils.logEvent('Kitchen lights automatically turned off');
    } else {
        node.log('Kitchen lights automatically turned off');
    }
} catch (error) {
    node.error(`Error logging auto-off: ${error.message}`);
}
return null;

try {
    const utils = global.get('kitchenUtils');
    const CONFIG = global.get('KITCHEN_CONFIG');

    if (!utils || !CONFIG) {
        node.error('Kitchen utilities or configuration not available');
        return null;
    }

    // Validate current state
    const stateValid = utils.validateKitchenState();

    if (stateValid) {
        utils.logEvent('Health check completed - all systems normal');
    } else {
        utils.logEvent('Health check found issues - state corrected', 'warn');
    }

    // Log current status
    const automation = flow.get('kitchenAutomationEnabled') !== false;
    const override = flow.get('kitchenManualOverride') === true;
    const brightness = flow.get('kitchenCurrentBrightness');

    utils.logEvent(`Status - Automation: ${automation}, Override: ${override}, Brightness: ${brightness}`);

    // Validate entities exist
    try {
        const haStates = global.get('homeassistant')?.homeAssistant?.states;
        if (haStates) {
            const lightState = haStates.get(CONFIG.ENTITIES.LIGHT);
            const motionState = haStates.get(CONFIG.ENTITIES.MOTION_SENSOR);
            const illuminanceState = haStates.get(CONFIG.ENTITIES.ILLUMINANCE_SENSOR);

            utils.logEvent(`Entity Status - Light: ${lightState ? 'OK' : 'MISSING'}, Motion: ${motionState ? 'OK' : 'MISSING'}, Illuminance: ${illuminanceState ? 'OK' : 'MISSING'}`);
        }
    } catch (entityError) {
        utils.logEvent(`Entity validation error: ${entityError.message}`, 'warn');
    }

    return null;

} catch (error) {
    const utils = global.get('kitchenUtils');
    if (utils) {
        utils.logEvent(`Health check failed: ${error.message}`, 'error');
    } else {
        node.error(`Critical health check failure: ${error.message}`);
    }
    return null;
}
